import {Component, OnInit} from '@angular/core';
import {FormGroup} from "@angular/forms";
import {TodoServiceService} from "../../services/todo-service.service";
import {ActivatedRoute} from "@angular/router";
import {ItodoItem} from "../../models/itodo-item";
import {Location} from "@angular/common";

@Component({
  selector: 'app-edit-todo-item',
  templateUrl: './edit-todo-item.component.html',
  styleUrls: ['./edit-todo-item.component.scss']
})
export class EditTodoItemComponent implements OnInit {
  todoForm: FormGroup;
  paramId: string | null | undefined

  constructor(private todoService: TodoServiceService, private route: ActivatedRoute, private location: Location) {
    this.todoForm = this.todoService.getTodoForm();
  }

  ngOnInit() {
    this.paramId = this.route.snapshot.paramMap.get('id');
    if (this.paramId)
      this.getItemDetails(this.route.snapshot.paramMap.get('id'));
  }

  private getItemDetails(id: string | null) {
    if (id)
      this.todoService.getDetails(id).subscribe((item: ItodoItem) => {
        this.todoForm = this.todoService.populateForm(item);
      })
  }

  onSubmitForm() {
    if (this.paramId)
      this.updateItem()
    else  {
      this.createTodoItem()
    }
  }

  createTodoItem() {
    this.todoService.createItem(this.todoForm.value).subscribe((item: ItodoItem) => {
      this.location.back();
    })
  }

  private updateItem() {
    this.todoService.updateItem(this.todoForm.value).subscribe((item: ItodoItem) => {
      this.location.back();
    })
  }

  isValid() {
    return !this.todoForm.valid
  }
}
<div class="place_at_center">
  <div class="container">

    <form [formGroup]="todoForm" >
      <div class="form-group">
        <label for="name">Name:</label>
        <input type="text" id="name" formControlName="name">
      </div>
      <div class="form-group">
        <label for="description">Description:</label>
        <textarea id="description" formControlName="description"></textarea>
      </div>
      <div class="form-group bordered">
        <label for="status">Status:</label>
        <mat-select id="status" formControlName="status">
          <mat-option value="PENDING">Pending</mat-option>
          <mat-option value="COMPLETE">Complete</mat-option>
          <mat-option value="IN_PROGRESS">In-progress</mat-option>
        </mat-select>

      </div>

      <div class="form-group bordered">
        <label for="priority">Priority:</label>
        <mat-select id="priority" formControlName="priority">
          <mat-option value="HIGH">High</mat-option>
          <mat-option value="MEDIUM">Medium</mat-option>
          <mat-option value="LOW">Low</mat-option>
        </mat-select>
      </div>
      <div class="form-group">
        <label for="tag">Tag:</label>
        <input type="text" id="tag" formControlName="tags">
      </div>
      <div class="form-group">
        <label for="date">Date:</label>
        <input type="date" id="date" formControlName="date">
      </div>
      <button [disabled]="isValid()" type="submit" (click)="onSubmitForm()">Submit</button>
    </form>


  </div>
</div>
