import {Component, OnInit} from '@angular/core';
import {FormGroup} from "@angular/forms";
import {TodoServiceService} from "../../services/todo-service.service";
import {ActivatedRoute} from "@angular/router";
import {ItodoItem} from "../../models/itodo-item";

@Component({
  selector: 'app-edit-todo-item',
  templateUrl: './edit-todo-item.component.html',
  styleUrls: ['./edit-todo-item.component.scss']
})
export class EditTodoItemComponent implements OnInit {
  todoForm = new FormGroup({});

  constructor(private todoService: TodoServiceService, private route: ActivatedRoute) {

  }

  ngOnInit() {
    this.getItemDetails(this.route.snapshot.paramMap.get('id'))
  }

  private getItemDetails(id: string | null) {
    if (id)
      this.todoService.getDetails(id).subscribe((item: ItodoItem) => {
        this.todoForm = this.todoService.populateForm(item);
      })
  }

  updateItem() {
    console.log(this.todoForm?.value);
  }
}
<div class="row" style="margin-top: 80px;">

  <div class="col-sm-12 card">
    <div class="card-header card-title">
      <span class="text text-primary">Update todo info</span>
      <a class="btn btn-primary pull-right" [routerLink]="['/agent-types']" href="#">All Agent Types</a>
    </div>
    <div class="card-body">
      <form [formGroup]="todoForm" class="form">
        <div class="form-group" hidden>
          <label for="id">ID</label>
          <input formControlName="id" class="form-control" type="number" id="id"/>
        </div>
        <div class="form-group">
          <label for="name">Name</label>
          <input formControlName="name" class="form-control" type="text" id="name"/>
        </div>
        <div class="form-group">
          <label for="description">Description</label>
          <input formControlName="description" class="form-control" type="text" id="description"/>
        </div>
        <div class="form-group">
          <label for="priority">Priority</label>
          <input formControlName="priority" class="form-control" type="text" id="priority"/>
        </div>

        <div class="form-group">
          <label for="status">Status</label>
          <input formControlName="status" class="form-control" type="text" id="status"/>
        </div>

        <div class="form-group">
          <label for="tags">Tags</label>
          <input formControlName="tags" class="form-control" type="text" id="tags"/>
        </div>

        <a class="btn btn-primary" href="#">Cancel</a>
        <button type="submit" class="btn btn-primary ml-2" (click)="updateItem()">Update</button>
      </form>
    </div>
  </div>

</div>
